package com.hack.leetcode;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;

/**
 * Created by rafthab on 1/22/17.
 * Input:
 s: "cbaebabacd" p: "abc"

 Output:
 [0, 6]

 Explanation:
 The substring with start index = 0 is "cba", which is an anagram of "abc".
 The substring with start index = 6 is "bac", which is an anagram of "abc".

 * Input:
 s: "abab" p: "ab"

 Output:
 [0, 1, 2]

 Explanation:
 The substring with start index = 0 is "ab", which is an anagram of "ab".
 The substring with start index = 1 is "ba", which is an anagram of "ab".
 The substring with start index = 2 is "ab", which is an anagram of "ab".

  * https://leetcode.com/problems/find-all-anagrams-in-a-string/
 */
public class FindAnagrams {

    private boolean isAnagram(String substr, String p) {
        int len = substr.length();
        HashMap<Character,Integer> hm = new HashMap<>();
        for(int i =0; i< len; i++) {
            // find elements and increment index of different characters

            if(hm.get(substr.charAt(i)) == null) {
                hm.put(substr.charAt(i), 1);
            } else {
                int currlen =  hm.get(substr.charAt(i));
                currlen++;
                hm.put(substr.charAt(i), currlen);
            }

            // find elements and decrement index of those characters

            if(hm.get(p.charAt(i)) == null) {
                hm.put(p.charAt(i), -1);
            } else {
                int currlen =  hm.get(p.charAt(i));
                currlen--;
                hm.put(p.charAt(i), currlen);
            }
        }

        for (Character key : hm.keySet()) {
            if(hm.get(key) != 0) {
                return false;
            }
        }

        return true;
    }
    public List<Integer> findAnagrams(String s, String p) {

        List<Integer> anagramIndexList = new ArrayList<>();
        int phraseLength = p.length();
        int strLength = s.length();

        for (int i = 0; i < strLength-phraseLength+1; i++) {
            if(isAnagram(s.substring(i, i+phraseLength), p)) {
                anagramIndexList.add(i);
            }
        }
        return anagramIndexList;

    }

    public static void main(String[] args) {
        FindAnagrams findAnagrams1 = new FindAnagrams();
        System.out.println(findAnagrams1.findAnagrams("",
                ""));
    }
}
